  /**
   @param collateralIn: amount of collateral (vt)
   */
  function openShort(
    uint256 marketId,
    uint256 collateralIn, 
    uint256 min_amount_out
  ) external _lock_ {

    if (duringMarketAssessment(marketId)){
      _logAssessmentShorts(marketId, msg.sender, collateralIn); 
      // queuedRepUpdates[msg.sender] += 1; 

    }
    //ShortBondingCurve shortZCB = ShortBondingCurve(shortZCBs[marketId]); 
    ShortBondingCurve shortZCB = markets[marketId].short;
    WrappedCollateral wCollateral = WrappedCollateral(shortZCB.getCollateral()); 

    // Mint wCollateral to this address
    wCollateral.mint(msg.sender, address(this), collateralIn); 
    wCollateral.approve(address(shortZCB), collateralIn); 

    // lendAmount: amount of zcb to borrow w/ shortZCB pricing
    (uint lendAmount, uint c) = shortZCB.calculateAmountGivenSell(collateralIn);
    _logTrades(marketId, msg.sender, collateralIn, true, false); 

    // zcb minted to shortzcb contract
    _lendForShort(msg.sender, marketId, lendAmount);

    if (duringMarketAssessment(marketId)) deduct_selling_fee(); 

    shortZCB.trustedShort(address(this), collateralIn, min_amount_out); 

    shortZCB.transfer(msg.sender, lendAmount); 


  }

  /// @notice called when short is being opened, it allows shortZCB contract to 
  /// "borrow" by minting new ZCB to the shortZCB contract. 
  /// @dev although minting new zcb is different from borrowing from existing zcb holders, 
  /// in the context of our bonding curve prediction 
  /// market this is alllowed since we just dont allow longZCB holders 
  /// to sell when liquidity dries up   
  function _lendForShort(
    address trader, 
    uint256 marketId, 
    uint256 requested_zcb
    ) internal {
    // BondingCurve zcb = BondingCurve(controller.getZCB_ad(marketId));
    BondingCurve zcb = markets[marketId].long;

    // Log debt data 
    CDP storage cdp = debt_pools[marketId];
    cdp.collateral_amount[trader] += requested_zcb; 
    cdp.borrowed_amount[trader] += requested_zcb;  
    cdp.total_debt += requested_zcb; 
    cdp.total_collateral += requested_zcb; //only ds 
    collateral_pot[marketId] += requested_zcb; //Total ds collateral 

    zcb.trustedDiscountedMint(address(markets[marketId].short), requested_zcb); 
  }

   function _repayForShort(
    address trader, 
    uint256 marketId, 
    uint256 repaying_zcb
    ) internal {
    // BondingCurve zcb = BondingCurve(controller.getZCB_ad(marketId));
    BondingCurve zcb = markets[marketId].long;

    CDP storage cdp = debt_pools[marketId];
    cdp.collateral_amount[trader] -= repaying_zcb; 
    cdp.borrowed_amount[trader] -= repaying_zcb; 
    cdp.total_debt -= repaying_zcb; 
    cdp.total_collateral -= repaying_zcb; 
    collateral_pot[marketId] -= repaying_zcb;
  }

  /// @notice called by user to repay shorts
  /// @param close_amount is in of shortZCB, 18dec 
  function closeShort(
    uint256 marketId, 
    uint256 close_amount, 
    uint256 min_collateral_out
    ) external _lock_ {



    ShortBondingCurve shortZCB = markets[marketId].short; //ShortBondingCurve(shortZCBs[marketId]);

    _repayForShort(msg.sender, marketId, close_amount);

    shortZCB.transferFrom(msg.sender, address(this), close_amount);

    // This will buy close_amount worth of longZCB to the shortZCB contract 
    (uint256 returned_collateral, uint256 tokenToBeBurned) = shortZCB.trustedClose(address(this), close_amount, min_collateral_out);  
     _logTrades(marketId, msg.sender, returned_collateral, false, false); 

    if(duringMarketAssessment(marketId)){
      assessment_shorts[marketId][msg.sender] -= returned_collateral;  
    }

    // Now burn the contract's bought longZCB imediately  
    markets[marketId].long.trustedDiscountedBurn(address(shortZCB), tokenToBeBurned);

    // Return collateral to trader 
    WrappedCollateral(shortZCB.getCollateral()).redeem(address(this), msg.sender, returned_collateral); 


  }

  function _logAssessmentShorts(uint256 marketId, address trader, uint256 collateralIn) internal {
    assessment_shorts[marketId][trader] += collateralIn; 
  }


  /// @notice called by traders when market is resolved  
  function redeemShortZCB(
    uint256 marketId 
    ) external _lock_ returns(uint256){
    require(!marketActive(marketId), "Market Active"); 
    // LinearShortZCB shortZCB = LinearShortZCB(shortZCBs[marketId]);
    ShortBondingCurve shortZCB = markets[marketId].short;
    uint256 shortZCB_redeem_amount = shortZCB.balanceOf(msg.sender); 
    shortZCB.trustedBurn(msg.sender, shortZCB_redeem_amount); 
    assert(controller.getVault(marketId).balanceOf(address(shortZCB))==0);

    uint256 long_redemption_price = get_redemption_price(marketId);
    uint256 redemption_price = long_redemption_price >= config.WAD ? 0 : config.WAD - long_redemption_price; 
    uint256 collateral_redeem_amount = redemption_price.mulWadDown(shortZCB_redeem_amount); 

    controller.redeem_transfer(collateral_redeem_amount, msg.sender, marketId); 
    return collateral_redeem_amount; 
  }

  /// @notice called by traders when market is denied or resolve before maturity 
  function redeemDeniedMarket(
    uint256 marketId, 
    bool isLong
  ) external _lock_ {
    require(!restriction_data[marketId].alive, "Market Still During Assessment"); // TODO

    uint256 collateral_amount;

    // Get collateral at stake in shorts, which will be directly given back to traders
    if(!isLong){
      ShortBondingCurve shortZCB = markets[marketId].short;
      require(shortZCB.balanceOf(msg.sender) >= 0, "Empty Balance");

      collateral_amount = shortTrades[marketId][msg.sender]; 
      delete shortTrades[marketId][msg.sender]; 

      //Burn all their balance
      shortZCB.trustedBurn( msg.sender,  shortZCB.balanceOf(msg.sender));
    } 

    // Get collateral at stake in longs, which will be directly given back to traders
    else {
      // BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
      BondingCurve zcb = markets[marketId].long;

      require(zcb.balanceOf(msg.sender) >= 0, "Empty Balance");

      if (isValidator(marketId, msg.sender)) {

        collateral_amount = validator_data[marketId].sales[msg.sender].mulWadDown(validator_data[marketId].avg_price);
        delete validator_data[marketId].sales[msg.sender];
      }
      else{
        collateral_amount = longTrades[marketId][msg.sender]; 
        delete longTrades[marketId][msg.sender]; 
      }

      // Burn all their balance 
      zcb.trustedBurn(msg.sender, zcb.balanceOf(msg.sender)); 
    }

    // Before redeem_transfer is called all funds for this instrument should be back in the vault
    // including dust from wCollateral 
    controller.redeem_transfer(collateral_amount, msg.sender, marketId);

    // queuedRepUpdates[msg.sender] -= 1; 

    //TODO need to check if last redeemer, so can kill market.

  }



  function get_redemption_price(uint256 marketId) public view returns(uint256){
    return redemption_prices[marketId]; 
  }

  /**
  @dev Redemption price, as calculated (only once) at maturity,
  depends on total_repayed/(principal + predetermined yield)
  If total_repayed = 0, redemption price is 0
  @param atLoss: defines circumstances where expected returns are higher than actual
  @param loss: facevalue - returned amount => non-negative always?
  @param extra_gain: any extra yield not factored during assessment. Is 0 yield is as expected
   */
  function update_redemption_price(
    uint256 marketId,
    bool atLoss, 
    uint256 extra_gain, 
    uint256 loss, 
    bool premature
  ) external  onlyController {  
    if (atLoss) assert(extra_gain == 0); 
    assert(debt_pools[marketId].total_debt == markets[marketId].short.totalSupply());

    // BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId))); // SOMEHOW GET ZCB
    BondingCurve zcb = markets[marketId].long;

    uint256 total_supply = zcb.getTotalZCB(); 
    uint256 total_shorts = (extra_gain >0) ? debt_pools[marketId].total_debt :0; 

    if(!atLoss){
      redemption_prices[marketId] = config.WAD + extra_gain.divWadDown(total_supply + total_shorts); 
      console.log('redemtionprice', redemption_prices[marketId]); 
    } 
    else {
      if (config.WAD <= loss.divWadDown(total_supply)){
        redemption_prices[marketId] = 0; 
      }
      else {
        redemption_prices[marketId] = config.WAD - loss.divWadDown(total_supply);
      }
    }

    deactivateMarket(marketId, atLoss, !premature); 
  }

  mapping(address=> uint8) queuedRepUpdates; 

  /// @notice trader will redeem entire balance of ZCB
  /// Needs to be called at maturity, market needs to be resolved first(from controller)
  function redeem(
    uint256 marketId
  ) public _lock_ returns(uint256){
    require(!marketActive(marketId), "Market Active"); 
    require(restriction_data[marketId].resolved, "Market not resolved"); 
    require(!redeemed[marketId][msg.sender], "Already Redeemed");
    redeemed[marketId][msg.sender] = true; 

    if (isValidator(marketId, msg.sender)) delete validator_data[marketId].sales[msg.sender]; 

    //BondingCurve zcb = BondingCurve(address(controller.getZCB(marketId)));
    BondingCurve zcb = markets[marketId].long;

    assert(controller.getVault(marketId).balanceOf(address(zcb))==0);

    uint256 zcb_redeem_amount = zcb.balanceOf(msg.sender); 
    zcb.trustedBurn(msg.sender, zcb_redeem_amount); 

    uint256 redemption_price = get_redemption_price(marketId); 
    uint256 collateral_redeem_amount = redemption_price.mulWadDown(zcb_redeem_amount); 

    if (!isValidator(marketId, msg.sender)) {
      bool increment = redemption_price >= config.WAD? true: false;
      controller.updateReputation(marketId, msg.sender, increment);
      // queuedRepUpdates[msg.sender] -=1; 
    }

    // Give out rewards only if reputation update debt is 0
    if (queuedRepUpdates[msg.sender] == 0) controller.redeem_transfer(collateral_redeem_amount, msg.sender, marketId); 

    return collateral_redeem_amount; 

  }
}

contracts/protocol/reputationtoken.sol.content:
pragma solidity ^0.8.4; 
//https://github.com/poap-xyz/poap-contracts/tree/master/contracts
import {ERC721} from "solmate/src/tokens/ERC721.sol";
import {Controller} from "./controller.sol";
import {BondingCurve} from "../bonds/bondingcurve.sol";
import "hardhat/console.sol";



contract ReputationNFT is ERC721 {
  mapping(uint256 => ReputationData) internal _reputation; // id to reputation
  mapping(address => uint256) internal _ownerToId;
  mapping(uint256 => TraderData[]) internal _marketData; // **MarketId to Market's data needed for calculating brier score.

  uint256 private nonce = 1;
  Controller controller;
  uint256 SCALE = 1e18;


  struct ReputationData {
    uint256 n; // number of markets participated in => regular uint256
    uint256 score; // averaged reputation score => 60.18
  }

  struct TraderData { // for each market
    address trader;
    uint256 tokensBought;
  }

    struct TopReputation{
    address trader; 
    uint256 score; 
  }

  uint256 private constant topRep = 100; 
  TopReputation[topRep] topReputations; 

  mapping(uint256=>mapping(address=>bool)) canTrade; //marketID-> address-> cantrade
  mapping(uint256=>bool) allowAll; 
  mapping(address=>bool) isUnique; 
  address[] unique_traders; 
  mapping(uint256=>mapping(address=>uint256)) public balances; // marketId => market manager address => how much collateral already bought.

  modifier onlyController() {
    require(msg.sender == address(controller));
    _;
  }

  constructor (
    address _controller
  ) ERC721("Debita Reputation Token", "DRT") {
    controller = Controller(_controller);
  }

  /**
   @notice incrementBalance
   */
  function incrementBalance(uint256 marketId, address trader, uint256 amount) external onlyController {
    balances[marketId][trader] += amount;
  }

  /**
   @notice called post reputation update
   */
  function removeBalance(uint256 marketId, address trader) external onlyController {
    delete balances[marketId][trader];
  }

  function _baseURI() internal pure returns (string memory baseURI) {
    baseURI = "";
  }

  function tokenURI(uint256 id) public view override returns (string memory) {
    require(_ownerOf[id] != address(0), "Invalid Identifier");

    string memory baseURI = _baseURI();
    return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, id)) : "";
  }

  function mint(address to) external {
    require(_ownerToId[to] == uint256(0), "can only mint one reputation token");
    super._mint(to, nonce);
    _ownerToId[to] = nonce;

    // Set default score, if this goes to 0 cannot trade
    _reputation[_ownerToId[to]].score = 1e18; 

    nonce++;
  }

  function getReputationScore(address owner) view external returns (uint256){
    require(_ownerToId[owner] != uint256(0), "No Id found");
    return _reputation[_ownerToId[owner]].score;
  }

  /**
   @notice add scores and stores topX 
   @param score: 60.18 format
   */
  function addScore(address to, uint256 score) external onlyController
   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    data.score = data.score + score; 

    storeTopX(data.score, to); 
  }

  function decrementScore(address to, uint256 score) external onlyController
   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];
    if (data.score <= score) data.score = 0; 
    else data.score = data.score - score; 

    storeTopX(data.score, to); 
  }

  /**
   @notice calculates average of scores added.
   @param score: 60.18 format
   */
  function addAverageScore(address to, uint256 score) external onlyController

   {
    require(_ownerToId[to] != uint256(0), "No Id found");

    ReputationData storage data = _reputation[_ownerToId[to]];

    if (data.n == 0) {
      data.score = score;
    } else {
      data.score = (data.score / data.n + score) / (data.n + 1);
    }

    data.n++;
  }

  /**
   @notice reset scores
   */
  function resetScore(address to) external {
    require(_ownerToId[to] != uint256(0), "No Id found");
    delete _reputation[_ownerToId[to]];
  }

  /// @notice called by controller when initiating market,
  function storeTopReputation(uint256 topX, uint256 marketId) external onlyController{
    if (getAvailableTopX() < topX) {
      allowAll[marketId] =true; 
      return; 
    }

    for (uint256 i; i<topX; i++){
      canTrade[marketId][topReputations[i].trader] = true;
    }

  }

  /// @notice gets the x's ranked score from all reputation scores 
  /// @dev returns 0 if topX is greater then avaiable nonzero rep scores-> everyone is allowed
  /// during reputation constraint periods 
  function getMinRepScore(uint256 topX, uint256 marketId) public view returns(uint256){
    if (getAvailableTopX() < topX) {
      return 0; 
    }
    return topReputations[topX].score; 

  }

  function getAvailableTopX() public view returns(uint256){
    return unique_traders.length; 
  }

  function getAvailableTraderNum() public view returns(uint256){
    return nonce -1; 
  }

  /// @notice whether trader is above reputation threshold 
  function traderCanTrade(uint256 marketId, address trader) external returns(bool){
    return allowAll[marketId]? true : canTrade[marketId][trader]; 
  }

  /// @notice called whenever a score is incremented   
  function storeTopX(uint256 score, address trader) internal {
    uint256 i = 0;

    for(i; i < topReputations.length; i++) {
        if(topReputations[i].score < score) {
            break;
        }
    }
    // shifting the array of position (getting rid of the last element) 
    for(uint j = topReputations.length - 1; j > i; j--) {
        topReputations[j].score = topReputations[j - 1].score;
        topReputations[j].trader = topReputations[j - 1].trader;
    }
    // update the new max element 
    topReputations[i].score = score;
    topReputations[i].trader = trader;

    if (isUnique[trader]) return; 
    isUnique[trader] = true; 
    unique_traders.push(trader);

  }




  function testStore() public view {
    for (uint i=0; i<10; i++){
      console.log('score', topReputations[i].score); 
    }
  }


}

contracts/bonds/LinearBondingCurve.sol.content:
pragma solidity ^0.8.4;

import {BondingCurve} from "./bondingcurve.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";
import "hardhat/console.sol";
import {config} from "../protocol/helpers.sol"; 

/// @notice y = a * x + b
/// @dev all computations are done in WAD space, any conversion if needed should be made in the parent contract  

contract LinearBondingCurve is BondingCurve {
  // ASSUMES 18 TRAILING DECIMALS IN UINT256
  using FixedPointMathLib for uint256;
  uint256  a;
  uint256  b;
  uint256  discount_cap; // maximum number of tokens for 
  uint256  b_initial; // b without discount cap 

  modifier _WAD_(uint256 amount) {
      require(config.isInWad(amount), "Not in wad or below minimum amount"); 
      _;
  }

  /// @param sigma is the proportion of P that is going to be bought at a discount
  /// param p,i,sigma all should be in WAD   
  constructor (
      string memory name,
      string memory symbol,
      address owner,
      address collateral, 
      uint256 P, 
      uint256 I,
      uint256 sigma
  ) BondingCurve(name, symbol, owner, collateral)_WAD_(P)  {
    _calculateInitCurveParams(P, I, sigma); 
  }

  /// @notice need to calculate initial curve params that takes into account
  /// validator rewards(from discounted zcb). Just skew up the initial price. 
  /// @param sigma is the proportion of P that is going to be bought at a discount  
  function _calculateInitCurveParams(uint256 P, uint256 I, uint256 sigma) internal virtual _WAD_(P) returns(uint256) {

    b = (2*P).divWadDown(P+I) - math_precision; 
    a = (math_precision-b).divWadDown(P+I); 

    // Calculate and store maximum tokens for discounts, 
    discount_cap = _calculatePurchaseReturn(P.mulWadDown(sigma));

    //get new initial price after saving for discounts 
    b = a.mulWadDown(discount_cap) + b;

    b_initial = (2*P).divWadDown(P+I) - math_precision; 

  }
  /**
   @dev tokens returned = [((a*s + b)^2 + 2*a*p)^(1/2) - (a*s + b)] / a
   @param amount: amount collateral in => needs to be converted to WAD before 
   tokens returned in WAD
   */
  function _calculatePurchaseReturn(uint256 amount)  internal view override virtual _WAD_(amount) returns(uint256) {
    uint256 s = totalSupplyAdjusted() ;

    uint256 x = ((a.mulWadDown(s) + b) ** 2)/math_precision; 
    uint256 y = 2*( a.mulWadDown(amount)); 
    uint256 x_y_sqrt = ((x+y)*math_precision).sqrt();
    uint256 z = (a.mulWadDown(s) + b); 
    uint256 result = (x_y_sqrt-z).divWadDown(a);

    return result;
  }


  /**
   @notice calculates area under curve from s-amount to s, is c(as-ac/2+b) where c is amount 
   @dev collateral tokens returned
   @param amount: amount of tokens burnt => WAD amount needs to be in 18 decimal 
   @dev returns amount of collateral tokens in WAD
   */
  function _calculateSaleReturn(uint256 amount) internal view override virtual _WAD_(amount) returns (uint256) {

    uint s = totalSupplyAdjusted();
    uint256 x = a.mulWadDown(s); 
    uint256 y = a.mulWadDown(amount)/2; 
    uint256 z = b + x - y; 
    uint256 result = amount.mulWadDown(z); 

    return result;
  }



  /// @notice calculates area under the curve from current supply to s+amount
  /// result = a * amount / 2  * (2* supply + amount) + b * amount
  /// @dev amount is in 60.18.
  /// returned in collateral decimals
  function _calcAreaUnderCurve(uint256 amount) internal view override virtual _WAD_(amount) returns(uint256){

    uint256 s = totalSupplyAdjusted(); 
    uint256 result = ( a.mulWadDown(amount) / 2 ).mulWadDown(2 * s + amount) + b.mulWadDown(amount); 

    return result; 
  }


  /**
   @param amount: amount added in 60.18
   @dev returns price in 60.18
   */
  function _calculateExpectedPrice(uint256 amount) internal view  override virtual returns (uint256 result) {

    uint256 s = totalSupplyAdjusted();

    result = (s + amount).mulWadDown(a) + b;
  }

  function _calculateDecreasedPrice(uint256 amount) view internal override virtual _WAD_(amount) returns (uint256 result) {
    result = (totalSupplyAdjusted() - amount).mulWadDown(a) + b;
  }


  /// @notice computes from arbitrary supply, from initial b
  function _calculateArbitraryPurchaseReturn(uint256 amount, uint256 supply)  internal view override virtual _WAD_(amount) returns(uint256) {
    uint256 s = supply; 

    uint256 x = ((a.mulWadDown(s) + b_initial) ** 2)/math_precision; 
    uint256 y = 2*( a.mulWadDown(amount)); 
    uint256 x_y_sqrt = ((x+y)*math_precision).sqrt();
    uint256 z = (a.mulWadDown(s) + b_initial); 
    uint256 result = (x_y_sqrt-z).divWadDown(a);

    return result;
  }


  function _calculateScore(uint256 priceOut, bool atLoss)view internal override virtual returns (uint256 score) {
    if (atLoss) {
      score = ((priceOut - math_precision) ** 2) / math_precision;
    } else {
      score = (priceOut ** 2) / math_precision;
    }
  }

  function _get_discount_cap() internal view virtual override returns(uint){
    return discount_cap; 
  }

  function _getParams() public view override returns(uint,uint){
    return (a,b); 
  }






}

