  using SafeERC20 for ERC20;


  constructor (
      string memory name,
      string memory symbol,
      address owner,
      address collateral, 
      address longZCBaddress, 
      uint256 marketId 

  ) ShortBondingCurve( name, symbol, owner, collateral, longZCBaddress, marketId) {

